import { FormEvent, useState } from "react";
import styles from "./styles.module.scss";

interface Task {
    title: string;
    done: boolean; 
    id: number;
}

export const Tasks: React.FC = () => {
  const [tasksTitle, setTaskTitle] = useState("");
  const [tasks, setTasks] = useState([] as Task[]);

  function handleSubmitAddTask(e:FormEvent) {
    e.preventDefault();

    if (tasksTitle.length < 3) {
        alert('Não é possível adicionar uma tarefa com menos de três letras.')
        return
    }

    //Adicionando a tarefa
    setTasks([
        ...tasks,
        { id: 1, title: tasksTitle} 
    ])

  }

  return (
    <section className={styles.container}>
      <form
        onSubmit={handleSubmitAddTask}
      >
        <div>
          <label htmlFor="task_title">Adicionar Tarefa</label>
          <input
            value={tasksTitle}
            onChange={(e) => setTaskTitle(e.target.value)}
            type="text"
            id="task_title"
            placeholder="Título da Tarefa"
          />
        </div>

        <button type="submit">Adicionar</button>
      </form>

      <ul>
        <li>
            <input type="checkbox" id="task" />
            <label htmlFor="task">tarefa 1</label>
        </li>
        <li>
            <input type="checkbox" id="task" />
            <label htmlFor="task">tarefa 2</label>
        </li>
      </ul>
    </section>
  );
};
