import { FormEvent, useEffect, useRef, useState } from "react";

export const Refs: React.FC = () => {
  const inputNameRef = useRef<HTMLInputElement>(null);
  const inputEmailRef = useRef<HTMLInputElement>(null);
  const inputPasswordRef = useRef<HTMLInputElement>(null);

  console.log(inputNameRef);

  useEffect(() => {
    console.log(inputNameRef);
  }, [inputNameRef]);

  function handleOnSubmit(e: FormEvent) {
    e.preventDefault(); //Evita que a p치gina seja carregada ao submeter o formul치rio
    console.log(inputNameRef.current?.value)
    console.log(inputEmailRef.current?.value)
    console.log(inputNameRef.current?.value)
  }

  return (
    <form
      onSubmit={handleOnSubmit}
      style={{
        padding: "2rem",
      }}
    >
      <h1>useRef</h1>

      <br />

      <input type="text" placeholder="Nome Completo" ref={inputNameRef} />
      <input type="email" placeholder="email" ref={inputEmailRef} />
      <input type="password" placeholder="senha" ref={inputPasswordRef} />

      <br />

      <button type="submit">Submeter</button>

      <br />
    </form>
  );
};
