import { useMemo, useState } from "react";

interface MemoizationProps {
  finacialData: {
    incomes: number[];
    outcomes: number[];
  };
}

export const Memoization: React.FC<MemoizationProps> = ({ finacialData }) => {
  // Estado que define se os valores devem ser mostrados ou não
  const [showValues, setShowValues] = useState(true);

  const totalIncomes = useMemo(() => {
    return finacialData.incomes.reduce((total, income) => {
        console.log('Calculando o total de receitas')
        return (total += income);
      }, 0);
  }, [finacialData.incomes]);

  const totalOutcomes = finacialData.outcomes.reduce((total, outcome) => {
    console.log('Calculando o total de despesas')
    return (total += outcome);
  }, 0);


  return (
    <div style={{ padding: "2rem" }}>
      <h1>Memoization</h1>

      <h2>usMemo</h2>

      <p>{`Total de receitas: R$ ${showValues ? totalIncomes : 'XXXXX'}`}</p>
      <br />
      <p>{`Total de despesas: R$ ${showValues ? totalOutcomes : 'XXXXX'}`}</p>
      <br />
      <br />
      <button
        onClick={() => {
          setShowValues(!showValues);
        }}
      >
        {showValues ? 'Ocultar valores' : 'Mostrar valores'}
      </button>
    </div>
  );
};
